mapper.select:
SELECT newsno, text, url, source, publish_date, impact, summary
FROM news
WHERE newsno = #{newsno}

SELECT newsno, impact, summary, url, source, publish_date, r FROM ( SELECT newsno, impact, summary, url, source, publish_date, rownum AS r FROM ( SELECT newsno, impact, summary, url, source, publish_date FROM news
WHERE UPPER(impact) LIKE '%' || UPPER(#{keyword}) || '%' OR UPPER(summary) LIKE '%' || UPPER(#{keyword}) || '%'
ORDER BY publish_date DESC ) ) WHERE r BETWEEN #{start} AND #{end}
