mapper.select:
SELECT surveyno, topic, startdate, enddate, cnt, visible, file1, file1saved, thumb1, size1
FROM survey
WHERE surveyno=#{surveyno}

mapper.update:
UPDATE survey 
SET topic=#{topic}, startdate=#{startdate}, cnt=#{cnt}, visible=#{visible}, file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE surveyno=#{surveyno}

mapper.select:
SELECT surveyno, topic, startdate, enddate, cnt, visible,  file1, file1saved, thumb1, size1, r
FROM (
    SELECT surveyno, topic, startdate, enddate, cnt, visible,  file1, file1saved, thumb1, size1, rownum as r
    FROM (
        SELECT surveyno, topic, startdate, enddate, cnt, visible,  file1, file1saved, thumb1, size1
        FROM survey
        ORDER BY startdate ASC
    )
    WHERE rownum <= #{end_num}
)
WHERE r >= #{start_num}

mapper.update:
UPDATE survey
SET cnt = cnt + 1
WHERE surveyno = #{surveyno}
